
public class Exo1_class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// permuter les valeurs de deux variables 
		char a = 'd', b = 'e', c;
		
		System.out.println("a = "+ a +", b = "+ b +", \nPermutation!!");
		
		c = a;
		a = b;
		b = c;
		
		
		System.out.println("a = "+ a +", b = "+ b);
		
		
		
		
		
		
		
		

	}

}
